import React, { useState } from "react";
import ReactCalendar from "react-calendar";
import "react-calendar/dist/Calendar.css";

function UserDashboard() {
  const [companies, setCompanies] = useState([
    {
      name: "Company A",
      lastFiveCommunications: [
        { type: "Email", date: "2023-09-05", notes: "Followed up on proposal details." },
        { type: "Phone Call", date: "2023-08-28", notes: "Discussed pricing options." },
      ],
      nextScheduledCommunication: { type: "Phone Call", date: "2024-01-15" },
      highlightDisabled: false,
    },
    {
      name: "Company B",
      lastFiveCommunications: [
        { type: "LinkedIn Message", date: "2023-10-15", notes: "Answered a query on projects." },
        { type: "Email", date: "2023-09-10", notes: "Shared pricing details." },
      ],
      nextScheduledCommunication: { type: "Email", date: "2024-02-01" },
      highlightDisabled: false,
    },
  ]);

  const [selectedDate, setSelectedDate] = useState(new Date());
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedCompany, setSelectedCompany] = useState(null);
  const [notifications, setNotifications] = useState({
    overdue: 1,
    today: 1,
  });

  // Handle Modal Open/Close
  const handleModalOpen = (company) => {
    setSelectedCompany(company);
    setModalOpen(true);
  };

  const handleModalClose = () => {
    setModalOpen(false);
    setSelectedCompany(null);
  };

  // Get Highlight Color
  const getHighlightColor = (nextDate) => {
    const today = new Date();
    const nextCommDate = new Date(nextDate);

    if (nextCommDate < today) return "red";
    if (
      nextCommDate.getFullYear() === today.getFullYear() &&
      nextCommDate.getMonth() === today.getMonth() &&
      nextCommDate.getDate() === today.getDate()
    )
      return "yellow";
    return "transparent";
  };

  // Render Tooltip for Notes
  const renderTooltip = (comm) => comm.notes || "No additional notes recorded.";

  return (
    <div className="container mx-auto p-4 bg-gray-100 rounded-lg">
      {/* Dashboard Header */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-4xl font-bold text-green-600">User Dashboard</h1>
        <div className="flex items-center space-x-4">
          <span className="relative">
            <svg className="w-8 h-8 text-blue-600" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M21 5a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V3a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2v2zM5 9h14l2 12H3l2-12z" />
            </svg>
            <span className="absolute top-0 right-0 inline-block w-5 h-5 bg-red-600 text-white text-xs rounded-full flex items-center justify-center">{notifications.overdue + notifications.today}</span>
          </span>
        </div>
      </div>

      {/* Notifications Section */}
      <div className="bg-yellow-100 p-4 mb-6 border-l-4 border-yellow-500">
        <h2 className="text-lg font-semibold text-orange-600">Notifications</h2>
        <div className="text-red-600">Overdue Communications: {notifications.overdue}</div>
        <div className="text-yellow-600">Today's Communications: {notifications.today}</div>
      </div>

      {/* Calendar View */}
      <div className="bg-blue-100 p-4 mb-6 rounded-lg text-center">
        <h2 className="text-lg font-semibold text-blue-600 mb-4">Calendar View</h2>
        <ReactCalendar
          onChange={setSelectedDate}
          value={selectedDate}
          tileContent={({ date }) => {
            const formattedDate = date.toISOString().split("T")[0];
            const hasCommunication = companies.some((comp) =>
              comp.lastFiveCommunications.some((comm) => comm.date === formattedDate)
            );
            return hasCommunication ? <div className="text-blue-600">â€¢</div> : null;
          }}
        />
        <div className="mt-4">Selected Date: {selectedDate.toLocaleDateString()}</div>
      </div>

      {/* Company Table */}
      <div className="bg-purple-100 p-4 rounded-lg">
        <h2 className="text-lg font-semibold text-purple-600 mb-4">Companies</h2>
        <table className="min-w-full table-auto">
          <thead>
            <tr>
              <th className="px-4 py-2 text-left">Company Name</th>
              <th className="px-4 py-2 text-left">Last Five Communications</th>
              <th className="px-4 py-2 text-left">Next Scheduled Communication</th>
              <th className="px-4 py-2 text-left">Actions</th>
              <th className="px-4 py-2 text-left">Disable Highlight</th>
            </tr>
          </thead>
          <tbody>
            {companies.map((company, index) => (
              <tr
                key={index}
                className={company.highlightDisabled ? "bg-white" : `bg-${getHighlightColor(company.nextScheduledCommunication.date)}`}
              >
                <td className="px-4 py-2">{company.name}</td>
                <td className="px-4 py-2">
                  {company.lastFiveCommunications.map((comm, idx) => (
                    <div key={idx} className="cursor-help mb-1">
                      <span className="underline" title={renderTooltip(comm)}>{comm.type} on {new Date(comm.date).toLocaleDateString()}</span>
                    </div>
                  ))}
                </td>
                <td className="px-4 py-2">
                  {company.nextScheduledCommunication.type} on {new Date(company.nextScheduledCommunication.date).toLocaleDateString()}
                </td>
                <td className="px-4 py-2">
                  <button className="bg-blue-600 text-white py-2 px-4 rounded-lg" onClick={() => handleModalOpen(company)}>
                    Log Communication
                  </button>
                </td>
                <td className="px-4 py-2">
                  <label className="flex items-center">
                    <input
                      type="checkbox"
                      className="form-checkbox"
                      checked={company.highlightDisabled}
                      onChange={() => setCompanies(prev => 
                        prev.map((c, idx) => 
                          idx === index ? { ...c, highlightDisabled: !c.highlightDisabled } : c
                        )
                      )}
                    />
                    <span className="ml-2">Disable</span>
                  </label>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modal for Logging Communication */}
      {modalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-lg shadow-lg max-w-sm">
            <h3 className="text-xl font-semibold text-blue-600">Log Communication</h3>
            <div className="mt-4">
              <label htmlFor="communication-type" className="block text-gray-700">Type of Communication</label>
              <select id="communication-type" className="w-full p-2 border rounded-lg mb-4">
                <option>LinkedIn Post</option>
                <option>Email</option>
                <option>Phone Call</option>
              </select>
            </div>
            <div className="mb-4">
              <label htmlFor="date" className="block text-gray-700">Date of Communication</label>
              <input type="date" id="date" className="w-full p-2 border rounded-lg" />
            </div>
            <div className="mb-4">
              <label htmlFor="notes" className="block text-gray-700">Notes</label>
              <textarea id="notes" className="w-full p-2 border rounded-lg" rows="3"></textarea>
            </div>
            <button
              onClick={() => {
                alert("Communication logged!");
                handleModalClose();
              }}
              className="w-full py-2 bg-green-600 text-white rounded-lg"
            >
              Submit
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default UserDashboard;
