import React, { useState } from "react";
import ReactCalendar from "react-calendar"; // Import the react-calendar library
import 'react-calendar/dist/Calendar.css'; // Import CSS for the calendar
import {
  Container,
  Typography,
  Card,
  CardContent,
  CardActions,
  Button,
  Grid,
  Tooltip,
  Badge,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  FormControlLabel,
  Switch,
  Divider
} from "@mui/material";
import NotificationsIcon from "@mui/icons-material/Notifications";
import AddIcon from "@mui/icons-material/Add";
import { format, parseISO } from "date-fns"; // to handle date formatting

function UserDashboard() {
  // State for communications
  const [communications, setCommunications] = useState([
    { company: "Company 1", type: "LinkedIn Post", date: "2023-09-01", isPast: true },
    { company: "Company 2", type: "Email", date: "2023-10-20", isPast: true },
    { company: "Company 3", type: "Phone Call", date: "2024-01-15", isPast: false }, // upcoming
    { company: "Company 4", type: "LinkedIn Message", date: "2024-02-10", isPast: false }, // upcoming
  ]);

  const [selectedDate, setSelectedDate] = useState(new Date());
  const [overdueCount, setOverdueCount] = useState(1); // Example overdue count
  const [companies, setCompanies] = useState([
    {
      name: 'Company A',
      communicationDueDate: '2024-12-25',
      highlightDisabled: false,
      communicationStatus: '',
    },
    {
      name: 'Company B',
      communicationDueDate: '2024-12-26',
      highlightDisabled: false,
      communicationStatus: '',
    },
    {
      name: 'Company C',
      communicationDueDate: '2024-12-30',
      highlightDisabled: false,
      communicationStatus: '',
    },
  ]);

  const handleHighlightToggle = (index) => {
    const updatedCompanies = companies.map((company, idx) => {
      if (idx === index) {
        company.highlightDisabled = !company.highlightDisabled; // Toggle the highlight visibility
      }
      return company;
    });
    setCompanies(updatedCompanies);
  };

  const getHighlightColor = (dueDate) => {
    const today = new Date();
    const dueDateObj = parseISO(dueDate);
    const isToday = format(today, 'yyyy-MM-dd') === format(dueDateObj, 'yyyy-MM-dd');
    const isOverdue = today > dueDateObj;

    if (isOverdue) return 'red';
    if (isToday) return 'yellow';
    return 'transparent'; // No highlight
  };

  const getCommunicationsByDate = (date) => {
    const formattedDate = date.toLocaleDateString();
    return communications.filter((comm) => comm.date === formattedDate);
  };

  const getRowColor = (date) => {
    const today = new Date().toISOString().slice(0, 10);
    const commDate = new Date(date).toISOString().slice(0, 10);
    if (commDate < today) return "#f8d7da"; // Red for overdue
    if (commDate === today) return "#fff3cd"; // Yellow for due today
    return "#ffffff"; // White for future dates
  };

  const handleAddCommunication = (companyName) => {
    alert(`Log communication for ${companyName}!`);
  };

  return (
    <Container>
      <Grid container alignItems="center" spacing={2} marginBottom={2}>
        <Grid item>
          <Badge
            badgeContent={overdueCount}
            color="error"
            overlap="circular"
            anchorOrigin={{
              vertical: "top",
              horizontal: "right",
            }}
          >
            <NotificationsIcon fontSize="large" />
          </Badge>
        </Grid>
        <Grid item>
          <Typography variant="h4" component="div">
            User Dashboard
          </Typography>
        </Grid>
      </Grid>

      {/* Calendar View */}
      <div style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
        <h3>Select a Date</h3>
        <ReactCalendar
          onChange={setSelectedDate}
          value={selectedDate}
          tileContent={({ date, view }) => {
            const communicationsForDate = getCommunicationsByDate(date);
            return (
              communicationsForDate.length > 0 && (
                <div>
                  <span>{communicationsForDate.length} comm.</span>
                </div>
              )
            );
          }}
        />
        <p>Selected Date: {selectedDate.toLocaleDateString()}</p>

        {/* Show communications for selected date */}
        <div className="communications">
          <h4>Communications for {selectedDate.toLocaleDateString()}</h4>
          {getCommunicationsByDate(selectedDate).length > 0 ? (
            getCommunicationsByDate(selectedDate).map((comm, index) => (
              <div key={index}>
                <p>{comm.type} - {comm.company}</p>
              </div>
            ))
          ) : (
            <p>No communications for this day.</p>
          )}
        </div>
      </div>

      {/* Communications Table */}
      <Paper sx={{ marginTop: 4 }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell><strong>Company Name</strong></TableCell>
              <TableCell><strong>Last Five Communications</strong></TableCell>
              <TableCell><strong>Next Scheduled Communication</strong></TableCell>
              <TableCell align="center"><strong>Actions</strong></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {communications.map((comm, index) => (
              <TableRow key={index} style={{ backgroundColor: getRowColor(comm.date) }}>
                <TableCell>{comm.company}</TableCell>
                <TableCell>
                  <Tooltip title="Past communications details" arrow>
                    <Typography variant="body2">
                      {comm.type} - {new Date(comm.date).toLocaleDateString()}
                    </Typography>
                  </Tooltip>
                </TableCell>
                <TableCell>
                  {comm.type} - {new Date(comm.date).toLocaleDateString()}
                </TableCell>
                <TableCell align="center">
                  <Button
                    variant="contained"
                    color="primary"
                    startIcon={<AddIcon />}
                    onClick={() => handleAddCommunication(comm.company)}
                  >
                    Log Communication
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>

      {/* Past and Upcoming Communications Lists */}
      <h3>Past Communications</h3>
      <ul>
        {communications
          .filter((comm) => comm.isPast)
          .map((comm, index) => (
            <li key={index}>
              {comm.company}: {comm.type} on {comm.date}
            </li>
          ))}
      </ul>

      <h3>Upcoming Communications</h3>
      <ul>
        {communications
          .filter((comm) => !comm.isPast)
          .map((comm, index) => (
            <li key={index}>
              {comm.company}: {comm.type} on {comm.date}
            </li>
          ))}
      </ul>

      {/* Company List with Highlight Toggle */}
      <Grid container spacing={2}>
        {companies.map((company, index) => (
          <Grid item xs={12} sm={6} md={4} key={index}>
            <Card
              sx={{
                backgroundColor: company.highlightDisabled
                  ? 'white'
                  : getHighlightColor(company.communicationDueDate),
                transition: 'background-color 0.3s',
              }}
            >
              <CardContent>
                <Typography variant="h6">{company.name}</Typography>
                <Typography>
                  Next Communication Due: {format(parseISO(company.communicationDueDate), 'MMMM dd, yyyy')}
                </Typography>
              </CardContent>
              <CardActions>
                {/* Disable Highlight Switch */}
                <FormControlLabel
                  control={
                    <Switch
                      checked={company.highlightDisabled}
                      onChange={() => handleHighlightToggle(index)}
                    />
                  }
                  label="Disable Highlight"
                />
              </CardActions>
            </Card>
            <Divider sx={{ marginTop: 2 }} />
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}

export default UserDashboard;
