import React, { useState } from "react";

// AdminDashboard Component
const AdminDashboard = () => {
  // State for Company Management
  const [companies, setCompanies] = useState([]);
  const [companyForm, setCompanyForm] = useState({
    name: "",
    location: "",
    linkedIn: "",
    emails: "",
    phoneNumbers: "",
    comments: "",
    periodicity: "2 weeks",
  });
  const [editingCompanyIndex, setEditingCompanyIndex] = useState(null);

  // State for Communication Method Management
  const initialMethods = [
    { name: "LinkedIn Post", description: "Post on LinkedIn", sequence: 1, mandatory: true },
    { name: "LinkedIn Message", description: "Message via LinkedIn", sequence: 2, mandatory: true },
    { name: "Email", description: "Send email to the company", sequence: 3, mandatory: true },
    { name: "Phone Call", description: "Make a phone call to the company", sequence: 4, mandatory: false },
    { name: "Other", description: "Other communication method", sequence: 5, mandatory: false },
  ];
  const [communicationMethods, setCommunicationMethods] = useState(initialMethods);
  const [methodForm, setMethodForm] = useState({
    name: "",
    description: "",
    sequence: "",
    mandatory: false,
  });
  const [editingMethodIndex, setEditingMethodIndex] = useState(null);

  // Form Handlers for Company Management
  const handleCompanyChange = (e) => {
    const { name, value } = e.target;
    setCompanyForm({ ...companyForm, [name]: value });
  };

  const handleCompanySubmit = (e) => {
    e.preventDefault();
    if (editingCompanyIndex !== null) {
      const updatedCompanies = [...companies];
      updatedCompanies[editingCompanyIndex] = { ...companyForm };
      setCompanies(updatedCompanies);
      setEditingCompanyIndex(null);
    } else {
      setCompanies([...companies, { ...companyForm }]);
    }
    resetCompanyForm();
  };

  const handleDeleteCompany = (index) => {
    setCompanies(companies.filter((_, i) => i !== index));
  };

  // Form Handlers for Communication Methods Management
  const handleMethodChange = (e) => {
    const { name, value } = e.target;
    const updatedValue = name === "mandatory" ? e.target.checked : value;
    setMethodForm({ ...methodForm, [name]: updatedValue });
  };

  const handleMethodSubmit = (e) => {
    e.preventDefault();
    if (editingMethodIndex !== null) {
      const updatedMethods = [...communicationMethods];
      updatedMethods[editingMethodIndex] = {
        ...methodForm,
        sequence: parseInt(methodForm.sequence),
      };
      setCommunicationMethods(updatedMethods);
      setEditingMethodIndex(null);
    } else {
      setCommunicationMethods([
        ...communicationMethods,
        { ...methodForm, sequence: parseInt(methodForm.sequence) },
      ]);
    }
    resetMethodForm();
  };

  const handleDeleteMethod = (index) => {
    setCommunicationMethods(communicationMethods.filter((_, i) => i !== index));
  };

  // Reset Forms
  const resetCompanyForm = () => {
    setCompanyForm({
      name: "",
      location: "",
      linkedIn: "",
      emails: "",
      phoneNumbers: "",
      comments: "",
      periodicity: "2 weeks",
    });
  };

  const resetMethodForm = () => {
    setMethodForm({ name: "", description: "", sequence: "", mandatory: false });
  };

  return (
    <div className="max-w-7xl mx-auto py-8 px-6">
      <h1 className="text-3xl font-semibold text-green-600 mb-8">Admin Dashboard</h1>

      {/* Company Management */}
      <section className="space-y-8">
        <h2 className="text-2xl font-semibold text-gray-800">Company Management</h2>
        <form onSubmit={handleCompanySubmit} className="space-y-6">
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
            {Object.keys(companyForm).map((key) => (
              <div key={key} className="flex flex-col">
                <label htmlFor={key} className="font-semibold text-sm mb-2">
                  {key.charAt(0).toUpperCase() + key.slice(1)}
                </label>
                {key === "comments" ? (
                  <textarea
                    id={key}
                    name={key}
                    value={companyForm[key]}
                    onChange={handleCompanyChange}
                    className="border-2 p-3 rounded-lg"
                  />
                ) : key === "periodicity" ? (
                  <select
                    id={key}
                    name={key}
                    value={companyForm[key]}
                    onChange={handleCompanyChange}
                    className="border-2 p-3 rounded-lg"
                  >
                    <option value="1 week">1 week</option>
                    <option value="2 weeks">2 weeks</option>
                    <option value="1 month">1 month</option>
                  </select>
                ) : (
                  <input
                    type={key === "linkedIn" || key === "emails" ? "url" : "text"}
                    id={key}
                    name={key}
                    value={companyForm[key]}
                    onChange={handleCompanyChange}
                    className="border-2 p-3 rounded-lg"
                  />
                )}
              </div>
            ))}
          </div>
          <button
            type="submit"
            className="w-full py-3 bg-blue-500 text-white rounded-lg mt-4"
          >
            {editingCompanyIndex !== null ? "Edit Company" : "Add Company"}
          </button>
        </form>

        {/* List of Companies */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 mt-6">
          {companies.map((company, index) => (
            <div key={index} className="bg-white border rounded-lg shadow-lg p-4">
              <h3 className="text-xl font-semibold">{company.name}</h3>
              <p><strong>Location: </strong>{company.location}</p>
              <p><strong>LinkedIn: </strong>{company.linkedIn}</p>
              <p><strong>Emails: </strong>{company.emails}</p>
              <p><strong>Phone Numbers: </strong>{company.phoneNumbers}</p>
              <p><strong>Comments: </strong>{company.comments}</p>
              <p><strong>Periodicity: </strong>{company.periodicity}</p>

              <div className="flex mt-4 space-x-2">
                <button
                  onClick={() => setEditingCompanyIndex(index)}
                  className="bg-blue-500 text-white px-4 py-2 rounded-lg"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDeleteCompany(index)}
                  className="bg-red-500 text-white px-4 py-2 rounded-lg"
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Communication Method Management */}
      <section className="space-y-8 mt-8">
        <h2 className="text-2xl font-semibold text-gray-800">Communication Method Management</h2>
        <form onSubmit={handleMethodSubmit} className="space-y-6">
          {Object.keys(methodForm).map((key) => (
            <div key={key} className="flex flex-col">
              <label htmlFor={key} className="font-semibold text-sm mb-2">
                {key.charAt(0).toUpperCase() + key.slice(1)}
              </label>
              {key === "mandatory" ? (
                <input
                  type="checkbox"
                  id={key}
                  name={key}
                  checked={methodForm[key]}
                  onChange={handleMethodChange}
                  className="ml-2"
                />
              ) : (
                <input
                  type={key === "sequence" ? "number" : "text"}
                  id={key}
                  name={key}
                  value={methodForm[key]}
                  onChange={handleMethodChange}
                  className="border-2 p-3 rounded-lg"
                />
              )}
            </div>
          ))}
          <button
            type="submit"
            className="w-full py-3 bg-blue-500 text-white rounded-lg mt-4"
          >
            {editingMethodIndex !== null ? "Edit Method" : "Add Method"}
          </button>
        </form>

        {/* List of Communication Methods */}
        <div className="mt-6">
          {communicationMethods.map((method, index) => (
            <div key={index} className="bg-white border rounded-lg shadow-lg p-4 mt-4">
              <h3 className="text-xl font-semibold">{method.name}</h3>
              <p><strong>Description: </strong>{method.description}</p>
              <p><strong>Sequence: </strong>{method.sequence}</p>
              <p><strong>Mandatory: </strong>{method.mandatory ? 'Yes' : 'No'}</p>

              <div className="flex mt-4 space-x-2">
                <button
                  onClick={() => setEditingMethodIndex(index)}
                  className="bg-blue-500 text-white px-4 py-2 rounded-lg"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDeleteMethod(index)}
                  className="bg-red-500 text-white px-4 py-2 rounded-lg"
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default AdminDashboard;
