import React, { useState } from 'react';
import { format } from 'date-fns';
import './index.css';

// Dashboard component that displays the user interface
const Dashboard = () => {
  const [companies, setCompanies] = useState([
    {
      name: "Company A",
      communications: [
        { type: "LinkedIn Post", date: "2023-09-05", notes: "Shared an update." },
        { type: "Email", date: "2023-08-20", notes: "Confirmed contract details." }
      ],
      nextCommunication: { type: "Phone Call", date: "2024-01-20" },
      isHighlightDisabled: false,
    },
    {
      name: "Company B",
      communications: [
        { type: "Email", date: "2023-11-05", notes: "Sent invoice." },
        { type: "LinkedIn Post", date: "2023-10-01", notes: "Update on services." }
      ],
      nextCommunication: { type: "Email", date: "2024-02-10" },
      isHighlightDisabled: false,
    }
  ]);

  const [modalOpen, setModalOpen] = useState(false);
  const [selectedCompany, setSelectedCompany] = useState(null);
  const [notifications, setNotifications] = useState({ overdue: 2, dueToday: 1 });

  const toggleHighlight = (index) => {
    setCompanies(companies.map((company, idx) => idx === index 
      ? { ...company, isHighlightDisabled: !company.isHighlightDisabled }
      : company
    ));
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-semibold mb-8 text-blue-600">User Dashboard</h1>

      {/* Notifications Section */}
      <Notifications overdue={notifications.overdue} dueToday={notifications.dueToday} />

      {/* Calendar Section */}
      <div className="my-6">
        <Calendar companies={companies} />
      </div>

      {/* Companies List */}
      <div className="mb-6">
        {companies.map((company, index) => (
          <CompanyRow
            key={index}
            company={company}
            index={index}
            toggleHighlight={toggleHighlight}
            setModalOpen={setModalOpen}
            setSelectedCompany={setSelectedCompany}
          />
        ))}
      </div>

      {/* Communication Modal */}
      {modalOpen && selectedCompany && (
        <CommunicationModal 
          company={selectedCompany}
          closeModal={() => setModalOpen(false)}
        />
      )}
    </div>
  );
};

// Company row for each company displaying info
const CompanyRow = ({ company, index, toggleHighlight, setModalOpen, setSelectedCompany }) => {
  const getHighlightColor = (date) => {
    const today = new Date();
    const nextDate = new Date(date);

    if (nextDate < today) return 'bg-red-200'; // Overdue
    if (nextDate.toLocaleDateString() === today.toLocaleDateString()) return 'bg-yellow-200'; // Due Today
    return '';
  };

  return (
    <div className={`flex justify-between items-center p-4 mb-4 bg-gray-100 rounded-lg shadow-lg ${getHighlightColor(company.nextCommunication.date)}`}>
      <div className="flex flex-col">
        <span className="font-semibold text-lg">{company.name}</span>
        <div>
          <strong>Last 5 Communications:</strong>
          <ul>
            {company.communications.slice(0, 5).map((comm, i) => (
              <li key={i} className="hover:bg-gray-300 p-2 cursor-pointer">
                <span>{comm.type} on {format(new Date(comm.date), 'dd MMM yyyy')}</span>
                <span className="text-sm block">{comm.notes}</span>
              </li>
            ))}
          </ul>
        </div>
      </div>
      <div className="text-sm text-gray-600">
        <strong>Next Scheduled Communication:</strong> {company.nextCommunication.type} on {format(new Date(company.nextCommunication.date), 'dd MMM yyyy')}
      </div>
      <div className="flex flex-col items-center justify-center">
        <button 
          onClick={() => {
            setModalOpen(true);
            setSelectedCompany(company);
          }}
          className="bg-green-600 text-white py-1 px-3 rounded-md mb-2"
        >
          Log Communication
        </button>
        <label className="flex items-center space-x-2">
          <input type="checkbox" checked={company.isHighlightDisabled} onChange={() => toggleHighlight(index)} />
          <span>Disable Highlight</span>
        </label>
      </div>
    </div>
  );
};

// Modal for communication input
const CommunicationModal = ({ company, closeModal }) => {
  const [communicationData, setCommunicationData] = useState({ type: '', date: '', notes: '' });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCommunicationData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = () => {
    console.log("New Communication:", communicationData);
    closeModal(); // Close modal after logging the communication.
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-10">
      <div className="bg-white p-6 rounded-lg w-full max-w-md">
        <h2 className="text-2xl font-semibold mb-4">Log Communication for {company.name}</h2>
        <div className="mb-4">
          <label htmlFor="type" className="block text-gray-700">Type of Communication</label>
          <select name="type" className="w-full p-2 border border-gray-300 rounded" value={communicationData.type} onChange={handleInputChange}>
            <option value="LinkedIn Post">LinkedIn Post</option>
            <option value="Email">Email</option>
            <option value="Phone Call">Phone Call</option>
          </select>
        </div>
        <div className="mb-4">
          <label htmlFor="date" className="block text-gray-700">Date of Communication</label>
          <input 
            type="date" 
            name="date" 
            value={communicationData.date} 
            onChange={handleInputChange} 
            className="w-full p-2 border border-gray-300 rounded"
          />
        </div>
        <div className="mb-4">
          <label htmlFor="notes" className="block text-gray-700">Notes</label>
          <textarea 
            name="notes" 
            value={communicationData.notes} 
            onChange={handleInputChange}
            rows="4" 
            className="w-full p-2 border border-gray-300 rounded"
          />
        </div>
        <button 
          onClick={handleSubmit} 
          className="w-full bg-blue-600 text-white py-2 rounded-md"
        >
          Submit
        </button>
        <button 
          onClick={closeModal}
          className="w-full bg-red-600 text-white py-2 rounded-md mt-4"
        >
          Close
        </button>
      </div>
    </div>
  );
};

// Notifications component
const Notifications = ({ overdue, dueToday }) => {
  return (
    <div className="bg-orange-100 p-4 rounded-lg mb-6">
      <div className="font-semibold text-lg text-orange-600">Notifications</div>
      <div className="mt-2">
        <div className="text-red-500">Overdue Communications: {overdue}</div>
        <div className="text-yellow-500">Today’s Communications: {dueToday}</div>
      </div>
    </div>
  );
};

// Calendar view component
const Calendar = ({ companies }) => {
  const renderTileContent = ({ date }) => {
    const formattedDate = date.toISOString().split('T')[0];
    const hasCommunication = companies.some(company =>
      company.communications.some(comm => comm.date === formattedDate)
    );
    return hasCommunication ? <div className="bg-red-500 text-white p-1 text-center rounded-full">•</div> : null;
  };

  return (
    <div className="mb-8 p-4 bg-blue-100 rounded-lg shadow-md">
      <h2 className="text-lg font-semibold text-blue-600 mb-4">Calendar View</h2>
      <ReactCalendar
        tileContent={renderTileContent}
        className="react-calendar mx-auto"
      />
    </div>
  );
};

export default UserDashboard;
