import React, { useState } from "react";
import {
  Container,
  Table,
  TableHead,
  TableBody,
  TableRow,
  TableCell,
  Typography,
  Paper,
  Tooltip,
  FormControlLabel,
  Switch,
} from "@mui/material";

function UserDashboard() {
  // State for companies and toggle highlights
  const [companies, setCompanies] = useState([
    {
      name: "Company A",
      lastFiveCommunications: [
        { type: "Email", date: "2023-09-05", notes: "Followed up on proposal details." },
        { type: "Phone Call", date: "2023-08-28", notes: "Discussed pricing options." },
        { type: "LinkedIn Post", date: "2023-08-10", notes: "Shared a relevant article." },
        { type: "Meeting", date: "2023-07-22", notes: "Quarterly performance review." },
        { type: "Email", date: "2023-07-01", notes: "Sent updated contract." },
      ],
      nextScheduledCommunication: { type: "Phone Call", date: "2024-01-15" },
      highlightDisabled: false,
    },
    {
      name: "Company B",
      lastFiveCommunications: [
        { type: "LinkedIn Message", date: "2023-10-15", notes: "Answered a query on projects." },
        { type: "Meeting", date: "2023-09-25", notes: "Introduced new features." },
        { type: "Email", date: "2023-09-10", notes: "Shared pricing details." },
        { type: "LinkedIn Post", date: "2023-08-05", notes: "Shared a client success story." },
        { type: "Phone Call", date: "2023-07-15", notes: "Confirmed delivery schedule." },
      ],
      nextScheduledCommunication: { type: "Email", date: "2024-02-01" },
      highlightDisabled: false,
    },
  ]);

  const getRowHighlight = (nextCommunicationDate) => {
    const today = new Date();
    const commDate = new Date(nextCommunicationDate);

    if (commDate < today) return "red"; // Overdue
    if (
      commDate.getFullYear() === today.getFullYear() &&
      commDate.getMonth() === today.getMonth() &&
      commDate.getDate() === today.getDate()
    )
      return "yellow"; // Due today
    return "transparent"; // No highlight
  };

  const handleHighlightToggle = (index) => {
    const updatedCompanies = companies.map((company, idx) => {
      if (idx === index) {
        company.highlightDisabled = !company.highlightDisabled;
      }
      return company;
    });
    setCompanies(updatedCompanies);
  };

  return (
    <Container>
      <Typography variant="h4" marginBottom={3}>
        User Dashboard
      </Typography>

      {/* Company Table */}
      <Paper elevation={3}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell><strong>Company Name</strong></TableCell>
              <TableCell><strong>Last Five Communications</strong></TableCell>
              <TableCell><strong>Next Scheduled Communication</strong></TableCell>
              <TableCell align="center"><strong>Actions</strong></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {companies.map((company, index) => (
              <TableRow
                key={index}
                style={{
                  backgroundColor: company.highlightDisabled
                    ? "white"
                    : getRowHighlight(company.nextScheduledCommunication.date),
                  transition: "background-color 0.3s",
                }}
              >
                {/* Company Name */}
                <TableCell>{company.name}</TableCell>

                {/* Last Five Communications */}
                <TableCell>
                  {company.lastFiveCommunications.map((comm, idx) => (
                    <Tooltip
                      title={comm.notes || "No additional notes recorded."}
                      arrow
                      key={idx}
                    >
                      <Typography
                        variant="body2"
                        component="span"
                        style={{ cursor: "help", textDecoration: "underline" }}
                      >
                        {comm.type} on {new Date(comm.date).toLocaleDateString()}
                        {idx < company.lastFiveCommunications.length - 1 ? ", " : ""}
                      </Typography>
                    </Tooltip>
                  ))}
                </TableCell>

                {/* Next Scheduled Communication */}
                <TableCell>
                  {company.nextScheduledCommunication.type} on{" "}
                  {new Date(company.nextScheduledCommunication.date).toLocaleDateString()}
                </TableCell>

                {/* Toggle Highlight */}
                <TableCell align="center">
                  <FormControlLabel
                    control={
                      <Switch
                        checked={company.highlightDisabled}
                        onChange={() => handleHighlightToggle(index)}
                      />
                    }
                    label="Disable Highlight"
                  />
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    </Container>
  );
}

export default UserDashboard;
