import React, { useState } from 'react';
import AdminDashboard from './components/Admin/AdminDashboard';
import UserDashboard from './components/User/UserDashboard';
import ReportingDashboard from './components/Reporting/ReportingDashboard';

const App = () => {
  // State to track the active component
  const [activeTab, setActiveTab] = useState('admin'); // Initial tab is 'admin'

  const handleTabSwitch = (tab) => {
    setActiveTab(tab);
  };

  // Inline styles for the container and buttons
  const containerStyle = {
    display: 'flex',
    flexDirection: 'column',  // Stack buttons vertically
    justifyContent: 'center', 
    alignItems: 'center',
    height: '100vh',  // Full screen height
  };

  const buttonStyle = {
    margin: '15px',        // Space between buttons
    padding: '10px 20px',  // Padding for button size
    fontSize: '16px',      // Font size
    border: 'none',        // Remove button border
    borderRadius: '5px',   // Rounded corners
    cursor: 'pointer',     // Pointer cursor on hover
    backgroundColor: '#4CAF50',  // Default button color
    color: 'white',        // Text color
    transition: 'background-color 0.3s ease',  // Smooth hover effect
  };

  // Hover style for buttons
  const buttonHoverStyle = {
    backgroundColor: '#45a049',  // Slightly darker color on hover
  };

  return (
    <div style={containerStyle}>
      <h1>Company Management Application</h1>

      {/* Navigation buttons */}
      <div>
        <button
          style={buttonStyle}
          onClick={() => handleTabSwitch('admin')}
          onMouseOver={(e) => (e.target.style.backgroundColor = buttonHoverStyle.backgroundColor)}
          onMouseOut={(e) => (e.target.style.backgroundColor = '#4CAF50')}
        >
          Admin Dashboard
        </button>
        <button
          style={buttonStyle}
          onClick={() => handleTabSwitch('user')}
          onMouseOver={(e) => (e.target.style.backgroundColor = buttonHoverStyle.backgroundColor)}
          onMouseOut={(e) => (e.target.style.backgroundColor = '#4CAF50')}
        >
          User Dashboard
        </button>
        <button
          style={buttonStyle}
          onClick={() => handleTabSwitch('reporting')}
          onMouseOver={(e) => (e.target.style.backgroundColor = buttonHoverStyle.backgroundColor)}
          onMouseOut={(e) => (e.target.style.backgroundColor = '#4CAF50')}
        >
          Reporting Dashboard
        </button>
      </div>

      {/* Conditional rendering based on active tab */}
      <div>
        {activeTab === 'admin' && <AdminDashboard />}
        {activeTab === 'user' && <UserDashboard />}
        {activeTab === 'reporting' && <ReportingDashboard />}
      </div>
    </div>
  );
};

export default App;
