// src/components/Admin/AdminDashboard.js

import React, { useState } from 'react';

const AdminDashboard = () => {
  
  // State variables to manage the input values
  
  const [companyName, setCompanyName] = useState('');
  const [companyLocation, setCompanyLocation] = useState('');
  const [linkedInProfile, setLinkedInProfile] = useState('');
  const [emails, setEmails] = useState('');
  const [phoneNumbers, setPhoneNumbers] = useState('');
  const [comments, setComments] = useState('');
  const [communicationPeriodicity, setCommunicationPeriodicity] = useState('2 weeks');
  
  // State to hold the list of companies
  const [companies, setCompanies] = useState([]);
  
  // State to handle editing a company
  const [editingCompanyId, setEditingCompanyId] = useState(null);

  // Handle form submission (Add or Edit)
  const handleSubmit = (event) => {
    event.preventDefault();
    const companyData = {
      companyName,
      companyLocation,
      linkedInProfile,
      emails,
      phoneNumbers,
      comments,
      communicationPeriodicity
    };

    if (editingCompanyId !== null) {
      // Editing an existing company
      const updatedCompanies = companies.map((company, index) =>
        index === editingCompanyId ? companyData : company
      );
      setCompanies(updatedCompanies);
      setEditingCompanyId(null); // Clear editing state
    } else {
      // Adding a new company
      setCompanies([...companies, companyData]);
    }

    // Clear form fields
    setCompanyName('');
    setCompanyLocation('');
    setLinkedInProfile('');
    setEmails('');
    setPhoneNumbers('');
    setComments('');
    setCommunicationPeriodicity('2 weeks');
  };

  // Handle Edit
  const handleEdit = (index) => {
    const companyToEdit = companies[index];
    setCompanyName(companyToEdit.companyName);
    setCompanyLocation(companyToEdit.companyLocation);
    setLinkedInProfile(companyToEdit.linkedInProfile);
    setEmails(companyToEdit.emails);
    setPhoneNumbers(companyToEdit.phoneNumbers);
    setComments(companyToEdit.comments);
    setCommunicationPeriodicity(companyToEdit.communicationPeriodicity);
    setEditingCompanyId(index);
  };

  // Handle Delete
  const handleDelete = (index) => {
    const updatedCompanies = companies.filter((_, i) => i !== index);
    setCompanies(updatedCompanies);
  };

  return (
    <div>
      <h1>Admin Dashboard - Company Management</h1>

      {/* Form for adding or editing companies */}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Company Name: </label>
          <input
            type="text"
            value={companyName}
            onChange={(e) => setCompanyName(e.target.value)}
            placeholder="Enter company name"
          />
        </div>

        <div>
          <label>Location: </label>
          <input
            type="text"
            value={companyLocation}
            onChange={(e) => setCompanyLocation(e.target.value)}
            placeholder="Enter company location"
          />
        </div>

        <div>
          <label>LinkedIn Profile: </label>
          <input
            type="url"
            value={linkedInProfile}
            onChange={(e) => setLinkedInProfile(e.target.value)}
            placeholder="Enter company LinkedIn profile URL"
          />
        </div>

        <div>
          <label>Emails: </label>
          <input
            type="email"
            value={emails}
            onChange={(e) => setEmails(e.target.value)}
            placeholder="Enter one or more email addresses (comma separated)"
          />
        </div>

        <div>
          <label>Phone Numbers: </label>
          <input
            type="text"
            value={phoneNumbers}
            onChange={(e) => setPhoneNumbers(e.target.value)}
            placeholder="Enter contact phone numbers"
          />
        </div>

        <div>
          <label>Comments: </label>
          <textarea
            value={comments}
            onChange={(e) => setComments(e.target.value)}
            placeholder="Additional company notes"
          />
        </div>

        <div>
          <label>Communication Periodicity: </label>
          <select
            value={communicationPeriodicity}
            onChange={(e) => setCommunicationPeriodicity(e.target.value)}
          >
            <option value="1 week">1 week</option>
            <option value="2 weeks">2 weeks</option>
            <option value="1 month">1 month</option>
            <option value="2 months">2 months</option>
            <option value="3 months">3 months</option>
          </select>
        </div>

        <button type="submit">{editingCompanyId !== null ? 'Update Company' : 'Save Company'}</button>
      </form>

      {/* List of Companies */}
      <h2>Companies</h2>
      <ul>
        {companies.map((company, index) => (
          <li key={index}>
            <h3>{company.companyName}</h3>
            <p>Location: {company.companyLocation}</p>
            <p>LinkedIn: <a href={company.linkedInProfile} target="_blank" rel="noopener noreferrer">View LinkedIn</a></p>
            <p>Emails: {company.emails}</p>
            <p>Phone Numbers: {company.phoneNumbers}</p>
            <p>Comments: {company.comments}</p>
            <p>Communication Periodicity: {company.communicationPeriodicity}</p>

            {/* Edit and Delete Buttons */}
            <button onClick={() => handleEdit(index)}>Edit</button>
            <button onClick={() => handleDelete(index)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDashboard;
