import React, { useState } from "react";
import ReactCalendar from "react-calendar"; // Import the react-calendar library
import 'react-calendar/dist/Calendar.css'; // Import CSS for the calendar
import {
  Container,
  Typography,
  Card,
  CardContent,
  CardActions,
  Button,
  Grid,
  Tooltip,
  Badge,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  FormControlLabel,
  Switch,
  Divider,
  Checkbox,
  Modal,
  Box,
  TextField
} from "@mui/material";
import NotificationsIcon from "@mui/icons-material/Notifications";
import AddIcon from "@mui/icons-material/Add";
import { format, parseISO } from "date-fns"; // to handle date formatting

// User Dashboard
function UserDashboard() {
  const [communications, setCommunications] = useState([
    { company: "Company 1", type: "LinkedIn Post", date: "2023-09-01", isPast: true },
    { company: "Company 2", type: "Email", date: "2023-10-20", isPast: true },
    { company: "Company 3", type: "Phone Call", date: "2024-01-15", isPast: false }, // upcoming
    { company: "Company 4", type: "LinkedIn Message", date: "2024-02-10", isPast: false }, // upcoming
  ]);

  const [companies, setCompanies] = useState([
    {
      name: 'Company A',
      communicationDueDate: '2024-12-25',
      highlightDisabled: false,
      communicationStatus: '',
    },
    {
      name: 'Company B',
      communicationDueDate: '2024-12-26',
      highlightDisabled: false,
      communicationStatus: '',
    },
    {
      name: 'Company C',
      communicationDueDate: '2024-12-30',
      highlightDisabled: false,
      communicationStatus: '',
    },
  ]);
  
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [selectedCompanies, setSelectedCompanies] = useState([]);
  const [modalOpen, setModalOpen] = useState(false);
  const [communicationData, setCommunicationData] = useState({
    type: "",
    date: "",
    notes: ""
  });

  // Handle Modal state and form handling
  const handleCommunicationModalOpen = () => setModalOpen(true);
  const handleCommunicationModalClose = () => setModalOpen(false);

  const handleCompanySelection = (company) => {
    setSelectedCompanies(prev => {
      if (prev.includes(company)) {
        return prev.filter(c => c !== company);
      } else {
        return [...prev, company];
      }
    });
  };

  const handleCommunicationSubmit = () => {
    const updatedCommunications = [...communications];
    selectedCompanies.forEach(company => {
      const companyCommunication = {
        company,
        type: communicationData.type,
        date: communicationData.date,
        isPast: new Date(communicationData.date) < new Date()
      };
      updatedCommunications.push(companyCommunication);

      // Reset highlights for selected companies
      const updatedCompanies = companies.map((item) => {
        if (selectedCompanies.includes(item.name)) {
          item.highlightDisabled = true; // Reset highlight upon submission
        }
        return item;
      });

      setCompanies(updatedCompanies);
    });

    setCommunications(updatedCommunications);
    setModalOpen(false); // Close the modal after submission
  };

  const getCommunicationsByDate = (date) => {
    const formattedDate = date.toLocaleDateString();
    return communications.filter((comm) => comm.date === formattedDate);
  };

  const getRowColor = (date) => {
    const today = new Date().toISOString().slice(0, 10);
    const commDate = new Date(date).toISOString().slice(0, 10);
    if (commDate < today) return "#f8d7da"; // Red for overdue
    if (commDate === today) return "#fff3cd"; // Yellow for due today
    return "#ffffff"; // White for future dates
  };

  const getHighlightColor = (dueDate) => {
    const today = new Date();
    const dueDateObj = parseISO(dueDate);
    const isToday = format(today, 'yyyy-MM-dd') === format(dueDateObj, 'yyyy-MM-dd');
    const isOverdue = today > dueDateObj;

    if (isOverdue) return 'red';
    if (isToday) return 'yellow';
    return 'transparent'; // No highlight
  };

  return (
    <Container>
      <Grid container alignItems="center" spacing={2} marginBottom={2}>
        <Grid item>
          <Badge
            badgeContent={1} // A placeholder value, you can track overdue items here
            color="error"
            overlap="circular"
            anchorOrigin={{
              vertical: "top",
              horizontal: "right",
            }}
          >
            <NotificationsIcon fontSize="large" />
          </Badge>
        </Grid>
        <Grid item>
          <Typography variant="h4" component="div">
            User Dashboard
          </Typography>
        </Grid>
      </Grid>

      {/* Calendar View */}
      <div style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
        <h3>Select a Date</h3>
        <ReactCalendar
          onChange={setSelectedDate}
          value={selectedDate}
        />
        <p>Selected Date: {selectedDate.toLocaleDateString()}</p>
      </div>

      {/* Communications Table */}
      <Paper sx={{ marginTop: 4 }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell><strong>Company Name</strong></TableCell>
              <TableCell><strong>Last Five Communications</strong></TableCell>
              <TableCell><strong>Next Scheduled Communication</strong></TableCell>
              <TableCell align="center"><strong>Actions</strong></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {communications.map((comm, index) => (
              <TableRow key={index} style={{ backgroundColor: getRowColor(comm.date) }}>
                <TableCell>{comm.company}</TableCell>
                <TableCell>
                  <Tooltip title="Past communications details" arrow>
                    <Typography variant="body2">
                      {comm.type} - {new Date(comm.date).toLocaleDateString()}
                    </Typography>
                  </Tooltip>
                </TableCell>
                <TableCell>
                  {comm.type} - {new Date(comm.date).toLocaleDateString()}
                </TableCell>
                <TableCell align="center">
                  <Button
                    variant="contained"
                    color="primary"
                    startIcon={<AddIcon />}
                    onClick={handleCommunicationModalOpen}
                  >
                    Log Communication
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>

      {/* Communication Modal */}
      <Modal
        open={modalOpen}
        onClose={handleCommunicationModalClose}
      >
        <Box sx={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          backgroundColor: 'white',
          padding: 3,
          boxShadow: 24,
          width: 400
        }}>
          <Typography variant="h6">Log Communication</Typography>
          <FormControlLabel
            control={<Checkbox
              checked={selectedCompanies.includes("Company A")}
              onChange={() => handleCompanySelection("Company A")}
            />}
            label="Company A"
          />
          <FormControlLabel
            control={<Checkbox
              checked={selectedCompanies.includes("Company B")}
              onChange={() => handleCompanySelection("Company B")}
            />}
            label="Company B"
          />
          <FormControlLabel
            control={<Checkbox
              checked={selectedCompanies.includes("Company C")}
              onChange={() => handleCompanySelection("Company C")}
            />}
            label="Company C"
          />
          <TextField
            label="Type of Communication"
            fullWidth
            margin="normal"
            value={communicationData.type}
            onChange={e => setCommunicationData({ ...communicationData, type: e.target.value })}
          />
          <TextField
            type="date"
            label="Date of Communication"
            fullWidth
            margin="normal"
            value={communicationData.date}
            onChange={e => setCommunicationData({ ...communicationData, date: e.target.value })}
          />
          <TextField
            label="Notes"
            multiline
            rows={4}
            fullWidth
            margin="normal"
            value={communicationData.notes}
            onChange={e => setCommunicationData({ ...communicationData, notes: e.target.value })}
          />
          <Grid container justifyContent="space-between" style={{ marginTop: "1rem" }}>
            <Button onClick={handleCommunicationModalClose} variant="outlined">Cancel</Button>
            <Button onClick={handleCommunicationSubmit} variant="contained" color="primary">Submit</Button>
          </Grid>
        </Box>
      </Modal>
    </Container>
  );
}

export default UserDashboard;
