import React, { useState, useEffect } from "react";
import ReactCalendar from "react-calendar";
import 'react-calendar/dist/Calendar.css';
import {
  Container,
  Typography,
  Card,
  CardContent,
  Button,
  Grid,
  Tooltip,
  Badge,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  FormControlLabel,
  Divider,
  Checkbox,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField
} from "@mui/material";
import NotificationsIcon from "@mui/icons-material/Notifications";
import AddIcon from "@mui/icons-material/Add";
import { format, parseISO, isToday, isBefore } from "date-fns"; // Removed addDays since it's not used

function UserDashboard() {
  const [communications] = useState([
    { company: "Company 1", type: "LinkedIn Post", date: "2023-09-01", isPast: true, notes: "Completed with good engagement" },
    { company: "Company 2", type: "Email", date: "2023-10-20", isPast: true, notes: "Follow-up needed" },
    { company: "Company 3", type: "Phone Call", date: "2024-01-15", isPast: false, notes: "" }, // upcoming
    { company: "Company 4", type: "LinkedIn Message", date: "2024-02-10", isPast: false, notes: "" }, // upcoming
  ]);

  const [selectedDate, setSelectedDate] = useState(new Date());
  const [companies, setCompanies] = useState([
    { name: 'Company A', communicationDueDate: '2024-12-25', highlightDisabled: false, communicationStatus: '', isSelected: false },
    { name: 'Company B', communicationDueDate: '2024-12-26', highlightDisabled: false, communicationStatus: '', isSelected: false },
    { name: 'Company C', communicationDueDate: '2024-12-30', highlightDisabled: false, communicationStatus: '', isSelected: false },
  ]);
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedCompanies, setSelectedCompanies] = useState([]);
  const [communicationDetails, setCommunicationDetails] = useState({ type: "", date: "", notes: "" });
  const [notifications, setNotifications] = useState({ overdue: 0, today: 0 });

  useEffect(() => {
    const overdue = companies.filter((company) => isBefore(parseISO(company.communicationDueDate), new Date()) && !company.highlightDisabled).length;
    const today = companies.filter((company) => isToday(parseISO(company.communicationDueDate)) && !company.highlightDisabled).length;
    setNotifications({ overdue, today });
  }, [companies]);

  const handleCompanySelect = (index) => {
    const updatedCompanies = [...companies];
    updatedCompanies[index].isSelected = !updatedCompanies[index].isSelected;
    setCompanies(updatedCompanies);
    setSelectedCompanies(updatedCompanies.filter(company => company.isSelected));
  };

  const openModal = () => {
    if (selectedCompanies.length > 0) {
      setModalOpen(true);
    } else {
      alert("Please select at least one company.");
    }
  };

  const handleSubmitCommunication = () => {
    const updatedCompanies = companies.map(company => {
      if (company.isSelected) {
        company.highlightDisabled = true;
      }
      return company;
    });
    setCompanies(updatedCompanies);
    setModalOpen(false);
    setCommunicationDetails({ type: "", date: "", notes: "" });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCommunicationDetails({ ...communicationDetails, [name]: value });
  };

  const getHighlightColor = (companyDueDate) => {
    const dueDate = parseISO(companyDueDate);
    if (isBefore(dueDate, new Date())) {
      return "red"; // Overdue
    } else if (isToday(dueDate)) {
      return "yellow"; // Due today
    }
    return "none";
  };
  

  return (
    <Container>
      <Grid container alignItems="center" spacing={2} marginBottom={2}>
        <Grid item>
          <Badge badgeContent={notifications.overdue + notifications.today} color="error" overlap="circular" anchorOrigin={{ vertical: "top", horizontal: "right" }}>
            <NotificationsIcon fontSize="large" />
          </Badge>
        </Grid>
        <Grid item>
          <Typography variant="h4" component="div">
            User Dashboard
          </Typography>
        </Grid>
      </Grid>

      <div style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
        <h3>Select a Date</h3>
        <ReactCalendar onChange={setSelectedDate} value={selectedDate} />
        <p>Selected Date: {selectedDate.toLocaleDateString()}</p>
      </div>

      <Grid container spacing={2}>
        {companies.map((company, index) => (
          <Grid item xs={12} sm={6} md={4} key={index}>
            <Card
              sx={{
                backgroundColor:
                  getHighlightColor(company.communicationDueDate) === "red"
                    ? "rgba(255, 99, 71, 0.3)" :
                  getHighlightColor(company.communicationDueDate) === "yellow"
                    ? "rgba(255, 255, 0, 0.3)" : "transparent",
                transition: "background-color 0.3s",
              }}
            >
              <CardContent>
                <Typography variant="h6">{company.name}</Typography>
                <Typography>
                  Next Communication Due: {format(parseISO(company.communicationDueDate), 'MMMM dd, yyyy')}
                </Typography>
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={company.isSelected}
                      onChange={() => handleCompanySelect(index)}
                      name={`select-${index}`}
                    />
                  }
                  label="Select"
                />
              </CardContent>
            </Card>
            <Divider sx={{ marginTop: 2 }} />
          </Grid>
        ))}
      </Grid>

      <Button variant="contained" color="primary" onClick={openModal} startIcon={<AddIcon />}>
        Communication Performed
      </Button>

      <Dialog open={modalOpen} onClose={() => setModalOpen(false)}>
        <DialogTitle>Log Communication</DialogTitle>
        <DialogContent>
          <TextField
            fullWidth
            label="Type of Communication"
            name="type"
            value={communicationDetails.type}
            onChange={handleInputChange}
            sx={{ marginBottom: 2 }}
          />
          <TextField
            fullWidth
            label="Date of Communication"
            type="date"
            name="date"
            value={communicationDetails.date}
            onChange={handleInputChange}
            InputLabelProps={{ shrink: true }}
            sx={{ marginBottom: 2 }}
          />
          <TextField
            fullWidth
            label="Add Notes"
            name="notes"
            value={communicationDetails.notes}
            onChange={handleInputChange}
            multiline
            rows={4}
            sx={{ marginBottom: 2 }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setModalOpen(false)} color="primary">
            Cancel
          </Button>
          <Button onClick={handleSubmitCommunication} color="primary" variant="contained">
            Submit
          </Button>
        </DialogActions>
      </Dialog>

      <Paper sx={{ marginTop: 4 }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell><strong>Company Name</strong></TableCell>
              <TableCell><strong>Last Five Communications</strong></TableCell>
              <TableCell><strong>Next Scheduled Communication</strong></TableCell>
              <TableCell align="center"><strong>Actions</strong></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {communications.map((comm, index) => (
              <TableRow key={index}>
                <TableCell>{comm.company}</TableCell>
                <TableCell>
                  {comm.isPast ? (
                    <Tooltip title={`Notes: ${comm.notes}`} arrow>
                      <Typography variant="body2" sx={{ cursor: "pointer", textDecoration: "underline" }}>
                        {comm.type} - {new Date(comm.date).toLocaleDateString()}
                      </Typography>
                    </Tooltip>
                  ) : (
                    <Typography variant="body2">{comm.type} - {new Date(comm.date).toLocaleDateString()}</Typography>
                  )}
                </TableCell>
                <TableCell>{comm.type} - {new Date(comm.date).toLocaleDateString()}</TableCell>
                <TableCell align="center">
                  <Button variant="contained" color="primary" startIcon={<AddIcon />}>
                    Log Communication
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
    </Container>
  );
}

export default UserDashboard;
