import React, { useState } from 'react'; 
import AdminDashboard from './components/Admin/AdminDashboard';
import UserDashboard from './components/User/UserDashboard';
import ReportingDashboard from './components/Reporting/ReportingDashboard';

const App = () => {
  // State to track the active component
  const [activeTab, setActiveTab] = useState('admin'); // Initial tab is 'admin'

  const handleTabSwitch = (tab) => {
    setActiveTab(tab);
  };

  // Inline styles
  const buttonStyle = {
    margin: '10px',
    padding: '10px 20px',
    fontSize: '16px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  };

  const containerStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh', // Full screen height
    flexDirection: 'column',
    minHeight: '100vh' // Stack buttons vertically
  };

  return (
    <div style={containerStyle}>
      <h1>Company Management Application</h1>

      {/* Navigation buttons */}
      <div>
        <button 
          style={{ ...buttonStyle, backgroundColor: '#4CAF50', color: 'white' }}
          onClick={() => handleTabSwitch('admin')}
        >
          Admin Dashboard
        </button>
        <button 
          style={{ ...buttonStyle, backgroundColor: '#008CBA', color: 'white' }}
          onClick={() => handleTabSwitch('user')}
        >
          User Dashboard
        </button>
        <button 
          style={{ ...buttonStyle, backgroundColor: '#f44336', color: 'white' }}
          onClick={() => handleTabSwitch('reporting')}
        >
          Reporting Dashboard
        </button>
      </div>

      {/* Conditional rendering based on active tab */}
      <div>
        {activeTab === 'admin' && <AdminDashboard />}
        {activeTab === 'user' && <UserDashboard />}
        {activeTab === 'reporting' && <ReportingDashboard />}
      </div>
    </div>
  );
};

export default App;
