import React, { useState } from "react";
import ReactCalendar from "react-calendar";
import "react-calendar/dist/Calendar.css";

// Helper function to format date
const formatDate = (date) => new Date(date).toLocaleDateString();

const UserDashboard = () => {
  const [companies, setCompanies] = useState([
    {
      name: "Company A",
      lastFiveCommunications: [
        { type: "LinkedIn Post", date: "2023-09-05", notes: "Shared an update on project status." },
        { type: "Email", date: "2023-08-28", notes: "Discussed pricing details." },
      ],
      nextScheduledCommunication: { type: "Phone Call", date: "2024-01-15" },
      highlightDisabled: false,
    },
    {
      name: "Company B",
      lastFiveCommunications: [
        { type: "LinkedIn Message", date: "2023-10-15", notes: "Followed up on initial contact." },
        { type: "Email", date: "2023-09-10", notes: "Confirmed details." },
      ],
      nextScheduledCommunication: { type: "Email", date: "2024-02-01" },
      highlightDisabled: false,
    },
  ]);
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedCompany, setSelectedCompany] = useState(null);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [notifications, setNotifications] = useState({
    overdue: 1,
    today: 1,
  });

  const handleModalOpen = (company) => {
    setSelectedCompany(company);
    setModalOpen(true);
  };

  const handleModalClose = () => {
    setModalOpen(false);
    setSelectedCompany(null);
  };

  const handleHighlightToggle = (companyIndex) => {
    setCompanies((prevCompanies) =>
      prevCompanies.map((company, index) =>
        index === companyIndex
          ? { ...company, highlightDisabled: !company.highlightDisabled }
          : company
      )
    );
  };

  // Get Highlight Color for the "Next Scheduled Communication" row
  const getHighlightColor = (nextDate) => {
    const today = new Date();
    const nextCommDate = new Date(nextDate);

    if (nextCommDate < today) return "bg-red-200"; // Overdue
    if (nextCommDate.toLocaleDateString() === today.toLocaleDateString()) return "bg-yellow-200"; // Due today
    return "bg-white"; // Default
  };

  const renderTooltip = (communication) => communication.notes || "No notes available.";

  return (
    <div className="container mx-auto p-6 bg-gray-100 rounded-lg space-y-6">
      <h1 className="text-3xl font-bold text-green-700">User Dashboard</h1>

      {/* Notifications Section */}
      <div className="bg-yellow-100 p-4 rounded-lg">
        <div className="flex justify-between items-center">
          <span className="text-lg font-semibold text-orange-600">Notifications</span>
          <span className="text-lg text-blue-600 font-semibold">
            Total: {notifications.overdue + notifications.today}
          </span>
        </div>
        <div className="mt-2">
          <div className="text-red-500">Overdue Communications: {notifications.overdue}</div>
          <div className="text-yellow-500">Today's Communications: {notifications.today}</div>
        </div>
      </div>

      {/* Calendar View */}
      <div className="p-4 bg-blue-100 rounded-lg shadow-md">
        <h2 className="text-lg font-semibold text-blue-600 mb-4">Calendar View</h2>
        <div className="text-center">
          <ReactCalendar
            onChange={setSelectedDate}
            value={selectedDate}
            tileContent={({ date }) => {
              const formattedDate = date.toISOString().split("T")[0];
              const hasCommunication = companies.some((comp) =>
                comp.lastFiveCommunications.some((comm) => comm.date === formattedDate)
              );
              return hasCommunication ? <span className="text-red-500">â€¢</span> : null;
            }}
          />
          <p className="mt-4 font-semibold text-lg">{formatDate(selectedDate)}</p>
        </div>
      </div>

      {/* Companies Table Section */}
      <div className="bg-purple-100 p-6 rounded-lg shadow-md">
        <h2 className="text-lg font-semibold text-purple-600 mb-4">Companies</h2>
        <table className="min-w-full table-auto">
          <thead>
            <tr className="bg-gray-200">
              <th className="px-4 py-2 text-left">Company Name</th>
              <th className="px-4 py-2 text-left">Last Five Communications</th>
              <th className="px-4 py-2 text-left">Next Scheduled Communication</th>
              <th className="px-4 py-2 text-left">Actions</th>
              <th className="px-4 py-2 text-left">Disable Highlight</th>
            </tr>
          </thead>
          <tbody>
            {companies.map((company, idx) => (
              <tr
                key={idx}
                className={company.highlightDisabled ? "bg-white" : `bg-opacity-50 ${getHighlightColor(company.nextScheduledCommunication.date)}`}
              >
                <td className="px-4 py-2">{company.name}</td>
                <td className="px-4 py-2">
                  {company.lastFiveCommunications.map((comm, idx) => (
                    <div key={idx} className="cursor-pointer mb-1 hover:bg-gray-100">
                      <span
                        className="underline"
                        title={renderTooltip(comm)}
                      >
                        {comm.type} on {formatDate(comm.date)}
                      </span>
                    </div>
                  ))}
                </td>
                <td className="px-4 py-2">
                  {company.nextScheduledCommunication.type} on{" "}
                  {formatDate(company.nextScheduledCommunication.date)}
                </td>
                <td className="px-4 py-2">
                  <button
                    className="bg-blue-600 text-white py-1 px-4 rounded-md"
                    onClick={() => handleModalOpen(company)}
                  >
                    Log Communication
                  </button>
                </td>
                <td className="px-4 py-2">
                  <label className="flex items-center cursor-pointer">
                    <input
                      type="checkbox"
                      checked={company.highlightDisabled}
                      onChange={() => handleHighlightToggle(idx)}
                      className="form-checkbox text-green-600"
                    />
                    <span className="ml-2">Disable</span>
                  </label>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modal for Logging Communication */}
      {modalOpen && selectedCompany && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-10">
          <div className="bg-white p-6 rounded-lg max-w-sm w-full shadow-md">
            <h3 className="text-2xl font-semibold text-green-600 mb-4">Log Communication</h3>
            <div className="mb-4">
              <label htmlFor="communication-type" className="block text-gray-700">Type of Communication</label>
              <select id="communication-type" className="w-full p-2 border border-gray-300 rounded-lg">
                <option>LinkedIn Post</option>
                <option>Email</option>
                <option>Phone Call</option>
              </select>
            </div>
            <div className="mb-4">
              <label htmlFor="date" className="block text-gray-700">Date of Communication</label>
              <input
                type="date"
                id="date"
                className="w-full p-2 border border-gray-300 rounded-lg"
              />
            </div>
            <div className="mb-4">
              <label htmlFor="notes" className="block text-gray-700">Notes</label>
              <textarea
                id="notes"
                className="w-full p-2 border border-gray-300 rounded-lg"
                rows="4"
              ></textarea>
            </div>
            <button
              onClick={() => {
                alert("Communication Logged Successfully!");
                handleModalClose();
              }}
              className="w-full py-2 bg-green-600 text-white rounded-lg"
            >
              Submit
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserDashboard;
