// src/components/Admin/AdminDashboard.js

import React, { useState } from 'react';

const AdminDashboard = () => {
    const [companyName, setCompanyName] = useState('');
    const [companyLocation, setCompanyLocation] = useState('');
    const [companyEmail, setCompanyEmail] = useState('');
    const [companyPhone, setCompanyPhone] = useState('');
    const [companyComments, setCompanyComments] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();
        // Normally you would send this data to an API or save it in your state
        console.log("Submitted company details:", { companyName, companyLocation, companyEmail, companyPhone, companyComments });
    };

    return (
        <div>
            <h1>Admin Dashboard</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Company Name: </label>
                    <input 
                        type="text" 
                        value={companyName}
                        onChange={(e) => setCompanyName(e.target.value)} 
                        placeholder="Enter company name" 
                    />
                </div>
                <div>
                    <label>Location: </label>
                    <input 
                        type="text" 
                        value={companyLocation}
                        onChange={(e) => setCompanyLocation(e.target.value)} 
                        placeholder="Enter company location" 
                    />
                </div>
                <div>
                    <label>Email: </label>
                    <input 
                        type="email" 
                        value={companyEmail}
                        onChange={(e) => setCompanyEmail(e.target.value)} 
                        placeholder="Enter company email" 
                    />
                </div>
                <div>
                    <label>Phone: </label>
                    <input 
                        type="text" 
                        value={companyPhone}
                        onChange={(e) => setCompanyPhone(e.target.value)} 
                        placeholder="Enter phone number" 
                    />
                </div>
                <div>
                    <label>Comments: </label>
                    <textarea 
                        value={companyComments} 
                        onChange={(e) => setCompanyComments(e.target.value)} 
                        placeholder="Add comments"
                    />
                </div>
                <button type="submit">Save Company</button>
            </form>
        </div>
    );
};

export default AdminDashboard;
