// src/components/Admin/AdminDa

import React, { useState } from "react";

const AdminDashboard = () => {
  const [companies, setCompanies] = useState([]);
  const [communicationMethods, setCommunicationMethods] = useState([
    { name: "LinkedIn Post", description: "Post on LinkedIn", sequence: 1, mandatory: true },
    { name: "LinkedIn Message", description: "Message via LinkedIn", sequence: 2, mandatory: true },
    { name: "Email", description: "Email communication", sequence: 3, mandatory: true },
    { name: "Phone Call", description: "Call via phone", sequence: 4, mandatory: true },
    { name: "Other", description: "Other communication methods", sequence: 5, mandatory: false },
  ]);

  // State for managing company form
  const [companyForm, setCompanyForm] = useState({
    name: "",
    location: "",
    linkedIn: "",
    emails: "",
    phoneNumbers: "",
    comments: "",
    periodicity: "2 weeks",
  });
  const [editingCompanyIndex, setEditingCompanyIndex] = useState(null);

  // State for managing communication methods form
  const [methodForm, setMethodForm] = useState({
    name: "",
    description: "",
    sequence: "",
    mandatory: false,
  });
  const [editingMethodIndex, setEditingMethodIndex] = useState(null);

  // Helper for handling company form changes
  const handleCompanyChange = (e) => {
    const { name, value } = e.target;
    setCompanyForm({ ...companyForm, [name]: value });
  };

  // Helper for handling method form changes
  const handleMethodChange = (e) => {
    const { name, value } = e.target;
    const updatedValue = name === "mandatory" ? e.target.checked : value;
    setMethodForm({ ...methodForm, [name]: updatedValue });
  };

  // Adding/Editing Company
  const handleCompanySubmit = (e) => {
    e.preventDefault();
    if (editingCompanyIndex !== null) {
      // Edit existing company
      const updatedCompanies = [...companies];
      updatedCompanies[editingCompanyIndex] = { ...companyForm };
      setCompanies(updatedCompanies);
      setEditingCompanyIndex(null);
    } else {
      // Add new company
      setCompanies([...companies, { ...companyForm }]);
    }
    setCompanyForm({
      name: "",
      location: "",
      linkedIn: "",
      emails: "",
      phoneNumbers: "",
      comments: "",
      periodicity: "2 weeks",
    });
  };

  // Adding/Editing Communication Method
  const handleMethodSubmit = (e) => {
    e.preventDefault();
    if (editingMethodIndex !== null) {
      const updatedMethods = [...communicationMethods];
      updatedMethods[editingMethodIndex] = { ...methodForm, sequence: parseInt(methodForm.sequence) };
      setCommunicationMethods(updatedMethods);
      setEditingMethodIndex(null);
    } else {
      setCommunicationMethods([
        ...communicationMethods,
        { ...methodForm, sequence: parseInt(methodForm.sequence) },
      ]);
    }
    setMethodForm({ name: "", description: "", sequence: "", mandatory: false });
  };

  // Deleting a company
  const handleDeleteCompany = (index) => {
    const updatedCompanies = companies.filter((_, i) => i !== index);
    setCompanies(updatedCompanies);
  };

  // Deleting a communication method
  const handleDeleteMethod = (index) => {
    const updatedMethods = communicationMethods.filter((_, i) => i !== index);
    setCommunicationMethods(updatedMethods);
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>

      {/* Company Management */}
      <section>
        <h2>Company Management</h2>
        <form onSubmit={handleCompanySubmit}>
          <div>
            <label>Company Name:</label>
            <input
              type="text"
              name="name"
              value={companyForm.name}
              onChange={handleCompanyChange}
              required
            />
          </div>
          <div>
            <label>Location:</label>
            <input
              type="text"
              name="location"
              value={companyForm.location}
              onChange={handleCompanyChange}
            />
          </div>
          <div>
            <label>LinkedIn Profile:</label>
            <input
              type="url"
              name="linkedIn"
              value={companyForm.linkedIn}
              onChange={handleCompanyChange}
            />
          </div>
          <div>
            <label>Emails:</label>
            <input
              type="text"
              name="emails"
              value={companyForm.emails}
              onChange={handleCompanyChange}
            />
          </div>
          <div>
            <label>Phone Numbers:</label>
            <input
              type="text"
              name="phoneNumbers"
              value={companyForm.phoneNumbers}
              onChange={handleCompanyChange}
            />
          </div>
          <div>
            <label>Comments:</label>
            <textarea
              name="comments"
              value={companyForm.comments}
              onChange={handleCompanyChange}
            />
          </div>
          <div>
            <label>Communication Periodicity:</label>
            <select
              name="periodicity"
              value={companyForm.periodicity}
              onChange={handleCompanyChange}
            >
              <option value="1 week">1 week</option>
              <option value="2 weeks">2 weeks</option>
              <option value="1 month">1 month</option>
            </select>
          </div>
          <button type="submit">
            {editingCompanyIndex !== null ? "Edit Company" : "Add Company"}
          </button>
        </form>

        {/* Company List */}
        <ul>
          {companies.map((company, index) => (
            <li key={index}>
              <p><strong>{company.name}</strong></p>
              <p>Location: {company.location}</p>
              <p>LinkedIn: {company.linkedIn}</p>
              <p>Emails: {company.emails}</p>
              <p>Phone Numbers: {company.phoneNumbers}</p>
              <p>Comments: {company.comments}</p>
              <p>Periodicity: {company.periodicity}</p>
              <button onClick={() => setEditingCompanyIndex(index)}>Edit</button>
              <button onClick={() => handleDeleteCompany(index)}>Delete</button>
            </li>
          ))}
        </ul>
      </section>

      {/* Communication Method Management */}
      <section>
        <h2>Communication Method Management</h2>
        <form onSubmit={handleMethodSubmit}>
          <div>
            <label>Method Name:</label>
            <input
              type="text"
              name="name"
              value={methodForm.name}
              onChange={handleMethodChange}
              required
            />
          </div>
          <div>
            <label>Description:</label>
            <input
              type="text"
              name="description"
              value={methodForm.description}
              onChange={handleMethodChange}
            />
          </div>
          <div>
            <label>Sequence:</label>
            <input
              type="number"
              name="sequence"
              value={methodForm.sequence}
              onChange={handleMethodChange}
              required
            />
          </div>
          <div>
            <label>
              <input
                type="checkbox"
                name="mandatory"
                checked={methodForm.mandatory}
                onChange={handleMethodChange}
              />
              Mandatory
            </label>
          </div>
          <button type="submit">
            {editingMethodIndex !== null ? "Edit Method" : "Add Method"}
          </button>
        </form>

        {/* Method List */}
        <ul>
          {communicationMethods
            .sort((a, b) => a.sequence - b.sequence)
            .map((method, index) => (
              <li key={index}>
                <p><strong>{method.name}</strong></p>
                <p>Description: {method.description}</p>
                <p>Sequence: {method.sequence}</p>
                <p>Mandatory: {method.mandatory ? "Yes" : "No"}</p>
                <button onClick={() => setEditingMethodIndex(index)}>Edit</button>
                <button onClick={() => handleDeleteMethod(index)}>Delete</button>
              </li>
            ))}
        </ul>
      </section>
    </div>
  );
};

export default AdminDashboard;
