import React, { useState } from "react";
import ReactCalendar from "react-calendar"; // Import the react-calendar library
import 'react-calendar/dist/Calendar.css'; // Import CSS for the calendar
import {
  Container,
  Typography,
  Card,
  CardContent,
  CardActions,
  Button,
  Grid,
  Tooltip,
  Badge,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  FormControlLabel,
  Switch,
  Divider,
  Checkbox,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField
} from "@mui/material";
import NotificationsIcon from "@mui/icons-material/Notifications";
import AddIcon from "@mui/icons-material/Add";
import { format, parseISO } from "date-fns"; // to handle date formatting

function UserDashboard() {
  // State for communications
  const [communications] = useState([
    { company: "Company 1", type: "LinkedIn Post", date: "2023-09-01", isPast: true, notes: "Completed with good engagement" },
    { company: "Company 2", type: "Email", date: "2023-10-20", isPast: true, notes: "Follow-up needed" },
    { company: "Company 3", type: "Phone Call", date: "2024-01-15", isPast: false, notes: "" }, // upcoming
    { company: "Company 4", type: "LinkedIn Message", date: "2024-02-10", isPast: false, notes: "" }, // upcoming
  ]);

  const [selectedDate, setSelectedDate] = useState(new Date());
  const [companies, setCompanies] = useState([
    {
      name: 'Company A',
      communicationDueDate: '2024-12-25',
      highlightDisabled: false,
      communicationStatus: '',
      isSelected: false, // Checkbox selection state
    },
    {
      name: 'Company B',
      communicationDueDate: '2024-12-26',
      highlightDisabled: false,
      communicationStatus: '',
      isSelected: false, // Checkbox selection state
    },
    {
      name: 'Company C',
      communicationDueDate: '2024-12-30',
      highlightDisabled: false,
      communicationStatus: '',
      isSelected: false, // Checkbox selection state
    },
  ]);
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedCompanies, setSelectedCompanies] = useState([]); // Track selected companies for the communication
  const [communicationDetails, setCommunicationDetails] = useState({
    type: "",
    date: "",
    notes: ""
  });

  // Handle checkbox selection for multiple companies
  const handleCompanySelect = (index) => {
    const updatedCompanies = [...companies];
    updatedCompanies[index].isSelected = !updatedCompanies[index].isSelected;
    setCompanies(updatedCompanies);

    // Update selected companies list
    setSelectedCompanies(updatedCompanies.filter(company => company.isSelected));
  };

  // Open communication modal
  const openModal = () => {
    if (selectedCompanies.length > 0) {
      setModalOpen(true);
    } else {
      alert("Please select at least one company.");
    }
  };

  // Handle form submission (log communication)
  const handleSubmitCommunication = () => {
    // Reset highlights (red or yellow) for selected companies
    const updatedCompanies = companies.map(company => {
      if (company.isSelected) {
        company.highlightDisabled = true; // Disable highlight after communication is logged
      }
      return company;
    });
    setCompanies(updatedCompanies);

    // You could also store the new communication in your database or state here
    console.log("Logged communication for:", selectedCompanies);
    console.log("Communication details:", communicationDetails);

    // Close modal after submission
    setModalOpen(false);
    setCommunicationDetails({
      type: "",
      date: "",
      notes: ""
    });
  };

  // Handle modal input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCommunicationDetails({
      ...communicationDetails,
      [name]: value
    });
  };

  return (
    <Container>
      <Grid container alignItems="center" spacing={2} marginBottom={2}>
        <Grid item>
          <Badge
            badgeContent={1}
            color="error"
            overlap="circular"
            anchorOrigin={{
              vertical: "top",
              horizontal: "right",
            }}
          >
            <NotificationsIcon fontSize="large" />
          </Badge>
        </Grid>
        <Grid item>
          <Typography variant="h4" component="div">
            User Dashboard
          </Typography>
        </Grid>
      </Grid>

      {/* Calendar View */}
      <div style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
        <h3>Select a Date</h3>
        <ReactCalendar
          onChange={setSelectedDate}
          value={selectedDate}
        />
        <p>Selected Date: {selectedDate.toLocaleDateString()}</p>
      </div>

      {/* Company List with Checkbox for Selection */}
      <Grid container spacing={2}>
        {companies.map((company, index) => (
          <Grid item xs={12} sm={6} md={4} key={index}>
            <Card
              sx={{
                backgroundColor: company.highlightDisabled ? 'white' : 'transparent',
                transition: 'background-color 0.3s',
              }}
            >
              <CardContent>
                <Typography variant="h6">{company.name}</Typography>
                <Typography>
                  Next Communication Due: {format(parseISO(company.communicationDueDate), 'MMMM dd, yyyy')}
                </Typography>
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={company.isSelected}
                      onChange={() => handleCompanySelect(index)}
                      name={`select-${index}`}
                    />
                  }
                  label="Select"
                />
              </CardContent>
            </Card>
            <Divider sx={{ marginTop: 2 }} />
          </Grid>
        ))}
      </Grid>

      {/* Button to Trigger Communication Logging */}
      <Button
        variant="contained"
        color="primary"
        onClick={openModal}
        startIcon={<AddIcon />}
      >
        Communication Performed
      </Button>

      {/* Communication Modal */}
      <Dialog open={modalOpen} onClose={() => setModalOpen(false)}>
        <DialogTitle>Log Communication</DialogTitle>
        <DialogContent>
          <TextField
            fullWidth
            label="Type of Communication"
            name="type"
            value={communicationDetails.type}
            onChange={handleInputChange}
            sx={{ marginBottom: 2 }}
          />
          <TextField
            fullWidth
            label="Date of Communication"
            type="date"
            name="date"
            value={communicationDetails.date}
            onChange={handleInputChange}
            InputLabelProps={{
              shrink: true,
            }}
            sx={{ marginBottom: 2 }}
          />
          <TextField
            fullWidth
            label="Add Notes"
            name="notes"
            value={communicationDetails.notes}
            onChange={handleInputChange}
            multiline
            rows={4}
            sx={{ marginBottom: 2 }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setModalOpen(false)} color="primary">
            Cancel
          </Button>
          <Button
            onClick={handleSubmitCommunication}
            color="primary"
            variant="contained"
          >
            Submit
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

export default UserDashboard;
