import React, { useState } from "react";
import ReactCalendar from "react-calendar";
import "react-calendar/dist/Calendar.css";

function UserDashboard() {
  // State for companies
  const [companies, setCompanies] = useState([
    {
      name: "Company A",
      lastFiveCommunications: [
        { type: "Email", date: "2023-09-05", notes: "Followed up on proposal details." },
        { type: "Phone Call", date: "2023-08-28", notes: "Discussed pricing options." },
      ],
      nextScheduledCommunication: { type: "Phone Call", date: "2024-01-15" },
      highlightDisabled: false,
    },
    {
      name: "Company B",
      lastFiveCommunications: [
        { type: "LinkedIn Message", date: "2023-10-15", notes: "Answered a query on projects." },
        { type: "Email", date: "2023-09-10", notes: "Shared pricing details." },
      ],
      nextScheduledCommunication: { type: "Email", date: "2024-02-01" },
      highlightDisabled: false,
    },
  ]);

  // State for calendar and notifications
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedCompany, setSelectedCompany] = useState(null);
  const [notifications, setNotifications] = useState({
    overdue: 1, // Example: 1 company has overdue tasks
    today: 1, // Example: 1 company has tasks due today
  });

  // Handle Modal Open/Close
  const handleModalOpen = (company) => {
    setSelectedCompany(company);
    setModalOpen(true);
  };

  const handleModalClose = () => {
    setModalOpen(false);
    setSelectedCompany(null);
  };

  // Get Highlight Color
  const getHighlightColor = (nextDate) => {
    const today = new Date();
    const nextCommDate = new Date(nextDate);

    if (nextCommDate < today) return "bg-red-500";
    if (
      nextCommDate.getFullYear() === today.getFullYear() &&
      nextCommDate.getMonth() === today.getMonth() &&
      nextCommDate.getDate() === today.getDate()
    )
      return "bg-yellow-500";
    return "bg-transparent";
  };

  // Handle Disable Highlight Toggle
  const handleToggleHighlight = (index) => {
    const updatedCompanies = companies.map((comp, idx) => {
      if (idx === index) {
        comp.highlightDisabled = !comp.highlightDisabled;
      }
      return comp;
    });
    setCompanies(updatedCompanies);
  };

  // Render Tooltip for Notes
  const renderTooltip = (comm) => comm.notes || "No additional notes recorded.";

  // Log New Communication (Action Modal Submission)
  const handleLogCommunication = () => {
    alert("Communication logged successfully!");
    handleModalClose();
  };

  return (
    <div className="container mx-auto p-6 bg-gray-100 rounded-xl">
      {/* Dashboard Header */}
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-4xl font-bold text-green-600">User Dashboard</h1>
        <div className="relative">
          <div className="absolute top-0 right-0">
            <button className="text-xl text-red-500">
              {notifications.overdue + notifications.today} Overdue Notifications
            </button>
          </div>
        </div>
      </div>

      {/* Notifications */}
      <div className="bg-orange-100 p-4 mb-8 border border-orange-300 rounded-lg">
        <h2 className="text-lg font-bold text-orange-600">Notifications</h2>
        <p className="text-red-600">Overdue Communications: {notifications.overdue}</p>
        <p className="text-yellow-600">Today's Communications: {notifications.today}</p>
      </div>

      {/* Calendar View */}
      <div className="bg-blue-100 p-4 mb-8 border border-blue-300 rounded-lg">
        <h2 className="text-lg font-bold text-blue-600">Calendar View</h2>
        <ReactCalendar
          onChange={setSelectedDate}
          value={selectedDate}
          tileContent={({ date }) => {
            const formattedDate = date.toISOString().split("T")[0];
            const hasCommunication = companies.some((comp) =>
              comp.lastFiveCommunications.some((comm) => comm.date === formattedDate)
            );
            return hasCommunication ? <div className="text-blue-600">â€¢</div> : null;
          }}
        />
        <p>Selected Date: {selectedDate.toLocaleDateString()}</p>
      </div>

      {/* Company Table */}
      <div className="bg-purple-100 p-4 rounded-lg mb-8">
        <h2 className="text-lg font-bold text-purple-700 mb-4">Companies</h2>
        <table className="w-full">
          <thead>
            <tr className="border-b border-purple-300">
              <th className="py-2 px-4 text-left">Company Name</th>
              <th className="py-2 px-4 text-left">Last Five Communications</th>
              <th className="py-2 px-4 text-left">Next Scheduled Communication</th>
              <th className="py-2 px-4 text-left">Actions</th>
              <th className="py-2 px-4 text-left">Disable Highlight</th>
            </tr>
          </thead>
          <tbody>
            {companies.map((company, index) => (
              <tr
                key={index}
                className={company.highlightDisabled ? "bg-white" : getHighlightColor(company.nextScheduledCommunication.date)}
              >
                <td className="py-2 px-4">{company.name}</td>
                <td className="py-2 px-4">
                  {company.lastFiveCommunications.map((comm, idx) => (
                    <div key={idx} className="cursor-pointer mb-2">
                      <div title={renderTooltip(comm)} className="text-sm">
                        {comm.type} on {new Date(comm.date).toLocaleDateString()}
                      </div>
                    </div>
                  ))}
                </td>
                <td className="py-2 px-4">
                  {company.nextScheduledCommunication.type} on{" "}
                  {new Date(company.nextScheduledCommunication.date).toLocaleDateString()}
                </td>
                <td className="py-2 px-4">
                  <button
                    onClick={() => handleModalOpen(company)}
                    className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600"
                  >
                    Log Communication
                  </button>
                </td>
                <td className="py-2 px-4">
                  <label className="flex items-center space-x-2">
                    <input
                      type="checkbox"
                      checked={company.highlightDisabled}
                      onChange={() => handleToggleHighlight(index)}
                      className="rounded-full"
                    />
                    <span>Disable</span>
                  </label>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Modal for Logging Communication */}
      {modalOpen && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96">
            <h2 className="text-xl font-bold text-blue-600 mb-4">Log Communication</h2>
            <form>
              <div className="mb-4">
                <label htmlFor="communication-type" className="block text-sm font-semibold">
                  Type of Communication
                </label>
                <select id="communication-type" className="w-full p-2 border border-gray-300 rounded">
                  <option value="Email">Email</option>
                  <option value="Phone Call">Phone Call</option>
                  <option value="LinkedIn Message">LinkedIn Message</option>
                  <option value="Meeting">Meeting</option>
                </select>
              </div>

              <div className="mb-4">
                <label htmlFor="communication-date" className="block text-sm font-semibold">
                  Date of Communication
                </label>
                <input
                  id="communication-date"
                  type="date"
                  className="w-full p-2 border border-gray-300 rounded"
                />
              </div>

              <div className="mb-4">
                <label htmlFor="communication-notes" className="block text-sm font-semibold">
                  Notes
                </label>
                <textarea
                  id="communication-notes"
                  rows="3"
                  className="w-full p-2 border border-gray-300 rounded"
                />
              </div>

              <div className="mb-4">
                <button
                  type="button"
                  onClick={handleLogCommunication}
                  className="w-full py-2 bg-purple-600 text-white rounded hover:bg-purple-700"
                >
                  Submit
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default UserDashboard;
